INSERT INTO `datingplaces` (`id_place`, `name_place`, `city`, `id_type`, `preview_text`, `detail_place`, `picture`, `date_created`, `count_number`) VALUES
(1, 'The Top Bar', 'Đà Nẵng', 1, '<p class=\"MsoNormal\">Đứng từ tr&ecirc;n cao bạn c&oacute; thể dễ d&agrave;ng ngắm nh&igrave;n th&agrave;nh phố biển l&uacute;c b&igrave;nh minh, ho&agrave;ng h&ocirc;n, hay khi l&ecirc;n đ&egrave;n. Tất cả đều l&agrave; những khung cảnh hẹn h&ograve; l&atilde;ng mạn m&agrave; c&aacute;c cặp đ&ocirc;i kh&ocirc;ng n&ecirc;n bỏ qua.</p>', '<p class=\"MsoNormal\"><span style=\"font-size: 10pt;\">Đứng từ tr&ecirc;n cao bạn c&oacute; thể dễ d&agrave;ng ngắm nh&igrave;n th&agrave;nh phố biển l&uacute;c b&igrave;nh minh, ho&agrave;ng h&ocirc;n, hay khi l&ecirc;n đ&egrave;n. Tất cả đều l&agrave; những khung cảnh hẹn h&ograve; l&atilde;ng mạn m&agrave; c&aacute;c cặp đ&ocirc;i kh&ocirc;ng n&ecirc;n bỏ qua.</span></p>\r\n<p>&nbsp;</p>\r\n<section class=\"loidan\" style=\"box-sizing: inherit; font-weight: bold; font-style: italic; margin-bottom: 15px; color: #333333; font-family: \'Open Sans\', sans-serif;\">Bạn c&oacute; thể hẹn người ấy ở c&aacute;c nh&agrave; h&agrave;ng, qu&aacute;n c&agrave; ph&ecirc; tr&ecirc;n cao để ngắm th&agrave;nh phố biển l&uacute;c b&igrave;nh minh, ho&agrave;ng h&ocirc;n, hay khi l&ecirc;n đ&egrave;n.</section>\r\n<section class=\"contents\" style=\"box-sizing: inherit; margin-bottom: 50px; padding-bottom: 50px; border-bottom: 1px solid #c8c8c8; color: #333333; font-family: \'Open Sans\', sans-serif;\"><span style=\"box-sizing: inherit;\"><span style=\"box-sizing: inherit; font-family: arial, helvetica, sans-serif;\"><span style=\"box-sizing: inherit; font-weight: bold;\">The Top Bar - A La Carte Da Nang Beach Hotel</span><br style=\"box-sizing: inherit;\" /><br style=\"box-sizing: inherit;\" />Địa chỉ: đường V&otilde; Nguy&ecirc;n Gi&aacute;p, quận Sơn Tr&agrave;.<br style=\"box-sizing: inherit;\" />&nbsp;<br style=\"box-sizing: inherit;\" /><img style=\"box-sizing: inherit; border: 0px; vertical-align: middle; display: initial; max-width: 100%; margin: 0px;\" src=\"https://cms.gotadi.com/getattachment/diem-den/Viet-nam/Mien-trung/thanh-pho-Da-Nang/Giai-tri/5-diem-hen-ho-lang-man-ngam-toan-canh-Da-Nang/5-diem-hen-ho-da-nang.jpg.aspx\" alt=\"\" /><br style=\"box-sizing: inherit;\" /><br style=\"box-sizing: inherit;\" />Đ&acirc;y l&agrave; địa điểm rất nổi tiếng ở Đ&agrave; Nẵng, nơi c&oacute; bể bơi s&aacute;t m&eacute;p tường hướng ra biển m&ecirc;nh m&ocirc;ng, c&ugrave;ng tầm nh&igrave;n tuyệt đẹp, c&oacute; thể chi&ecirc;m ngưỡng cảnh b&igrave;nh minh v&agrave; ho&agrave;ng h&ocirc;n ấn tượng nhất ở th&agrave;nh phố biển. Ngo&agrave;i bể bơi, &iacute;t người để &yacute; rằng, tr&ecirc;n s&acirc;n thượng của kh&aacute;ch sạn c&ograve;n c&oacute; một qu&aacute;n bar, phục vụ nhiều đồ uống đa dạng, chất lượng v&agrave; gi&aacute; cả vừa phải. Bạn c&oacute; thể thư th&aacute;i nằm d&agrave;i tr&ecirc;n những chiếc ghế đệm để ngắm nh&igrave;n th&agrave;nh phố v&agrave; cảnh biển trong một ng&agrave;y lười biếng.<br style=\"box-sizing: inherit;\" />D&ugrave; vậy, bạn được khuy&ecirc;n rằng chỉ n&ecirc;n đến đ&acirc;y v&agrave;o ban ng&agrave;y, bởi khi đ&ecirc;m xuống, qu&aacute;n hướng ra biển n&ecirc;n kh&aacute; tối, cảnh kh&ocirc;ng đẹp như ban ng&agrave;y.<br style=\"box-sizing: inherit;\" /></span></span></section>', 'datingplaces-1544757171.png', '2018-12-13 20:12:51', 0),
(2, 'Sky 36', 'Đà Nẵng', 1, '<p><span style=\"color: #333333; font-family: arial, helvetica, sans-serif;\">Sky 36 được khen ngợi v&igrave; vị tr&iacute; đẹp, view ấn tượng, d&agrave;nh cho những ai muốn ngồi h&oacute;ng gi&oacute;, ngắm to&agrave;n cảnh th&agrave;nh phố từ l&uacute;c ho&agrave;ng h&ocirc;n tới khi đ&ecirc;m muộn.</span></p>', '<p><span style=\"color: #333333; font-family: arial, helvetica, sans-serif;\">Địa chỉ: tầng 35 -36, Novotel Danang Premier Han River, 36 Bạch Đằng.</span><br style=\"box-sizing: inherit; color: #333333; font-family: arial, helvetica, sans-serif;\" /><span style=\"color: #333333; font-family: arial, helvetica, sans-serif;\">&nbsp;</span><br style=\"box-sizing: inherit; color: #333333; font-family: arial, helvetica, sans-serif;\" /><img style=\"box-sizing: inherit; border: 0px; vertical-align: middle; display: initial; max-width: 100%; margin: 0px; color: #333333; font-family: arial, helvetica, sans-serif;\" src=\"https://cms.gotadi.com/getattachment/diem-den/Viet-nam/Mien-trung/thanh-pho-Da-Nang/Giai-tri/5-diem-hen-ho-lang-man-ngam-toan-canh-Da-Nang/5-diem-hen-ho-da-nang-2.jpg.aspx\" alt=\"\" /><br style=\"box-sizing: inherit; color: #333333; font-family: arial, helvetica, sans-serif;\" /><br style=\"box-sizing: inherit; color: #333333; font-family: arial, helvetica, sans-serif;\" /><span style=\"color: #333333; font-family: arial, helvetica, sans-serif;\">Qu&aacute;n bar mở được một thời gian nhưng vẫn chưa hết sức n&oacute;ng với d&acirc;n chơi Đ&agrave; Nẵng. Sky 36 được khen ngợi v&igrave; vị tr&iacute; đẹp, view ấn tượng, d&agrave;nh cho những ai muốn ngồi h&oacute;ng gi&oacute;, ngắm to&agrave;n cảnh th&agrave;nh phố từ l&uacute;c ho&agrave;ng h&ocirc;n tới khi đ&ecirc;m muộn. Từ đ&acirc;y, bạn c&oacute; tầm nh&igrave;n 360 độ, ngắm nh&igrave;n s&ocirc;ng H&agrave;n, c&aacute;c c&acirc;y cầu nổi tiếng hay c&aacute;c t&ograve;a nh&agrave; cao tầng khi l&ecirc;n đ&egrave;n.</span><br style=\"box-sizing: inherit; color: #333333; font-family: arial, helvetica, sans-serif;\" /><span style=\"color: #333333; font-family: arial, helvetica, sans-serif;\">Sky36 thường xuy&ecirc;n mời được c&aacute;c DJ nổi tiếng, nước pha chế kh&aacute; ngon, th&aacute;i độ phục vụ của nh&acirc;n vi&ecirc;n kh&aacute; nhiệt t&igrave;nh.</span></p>', 'datingplaces-1544757276.jpg', '2018-12-13 20:14:36', 0),
(3, 'Sky View Lounge', 'Đà Nẵng', 1, '<p><span style=\"color: #333333; font-family: arial, helvetica, sans-serif;\">Nằm ở tầng 23 t&ograve;a cao ốc kh&aacute;ch sạn One Opera Danang (Hoang Anh Gia Lai cũ), đ&acirc;y l&agrave; khu vực được nhiều bạn trẻ lựa chọn khi muốn ngắm th&agrave;nh phố.</span></p>', '<p><span style=\"box-sizing: inherit; font-weight: bold; color: #333333; font-family: arial, helvetica, sans-serif;\">Sky View Lounge</span><br style=\"box-sizing: inherit; color: #333333; font-family: arial, helvetica, sans-serif;\" /><br style=\"box-sizing: inherit; color: #333333; font-family: arial, helvetica, sans-serif;\" /><span style=\"color: #333333; font-family: arial, helvetica, sans-serif;\">Địa chỉ: tầng 23, số 1 Nguyễn Văn Linh.</span><br style=\"box-sizing: inherit; color: #333333; font-family: arial, helvetica, sans-serif;\" /><span style=\"color: #333333; font-family: arial, helvetica, sans-serif;\">&nbsp;</span><br style=\"box-sizing: inherit; color: #333333; font-family: arial, helvetica, sans-serif;\" /><img style=\"box-sizing: inherit; border: 0px; vertical-align: middle; display: initial; max-width: 100%; margin: 0px; color: #333333; font-family: arial, helvetica, sans-serif;\" src=\"https://cms.gotadi.com/getattachment/diem-den/Viet-nam/Mien-trung/thanh-pho-Da-Nang/Giai-tri/5-diem-hen-ho-lang-man-ngam-toan-canh-Da-Nang/5-diem-hen-ho-da-nang-5.jpg.aspx\" alt=\"\" /><br style=\"box-sizing: inherit; color: #333333; font-family: arial, helvetica, sans-serif;\" /><br style=\"box-sizing: inherit; color: #333333; font-family: arial, helvetica, sans-serif;\" /><span style=\"color: #333333; font-family: arial, helvetica, sans-serif;\">Nằm ở tầng 23 t&ograve;a cao ốc kh&aacute;ch sạn One Opera Danang (Hoang Anh Gia Lai cũ), đ&acirc;y l&agrave; khu vực được nhiều bạn trẻ lựa chọn khi muốn ngắm th&agrave;nh phố. Tới qu&aacute;n, bạn c&oacute; thể ngồi bất kỳ vị tr&iacute; n&agrave;o nh&acirc;m nhi đồ uống v&agrave; tận hưởng kh&ocirc;ng gian tho&aacute;ng đ&atilde;ng.</span><br style=\"box-sizing: inherit; color: #333333; font-family: arial, helvetica, sans-serif;\" /><br style=\"box-sizing: inherit; color: #333333; font-family: arial, helvetica, sans-serif;\" /><span style=\"color: #333333; font-family: arial, helvetica, sans-serif;\">Kh&ocirc;ng chỉ vậy, đ&acirc;y cũng l&agrave; nơi bạn c&oacute; thể ngắm những chiếc phi cơ cất v&agrave; hạ c&aacute;nh ngay tại s&acirc;n bay quốc tế Đ&agrave; Nẵng. Sky View Lounge ph&ugrave; hợp cho những người y&ecirc;u nhiếp ảnh. Từ đ&acirc;y, bạn sẽ nh&igrave;n thấy bờ s&ocirc;ng H&agrave;n l&atilde;ng mạn, eo biển Xu&acirc;n Thiều tr&ecirc;n đường Nguyễn Tất Th&agrave;nh, đ&egrave;o Hải V&acirc;n v&agrave; cả Ngũ H&agrave;nh Sơn.</span></p>', 'datingplaces-1544757364.jpg', '2018-12-13 20:16:04', 0),
(4, 'Ngắm cầu tình yêu Đà Nẵng lãng mạn như trong phim Hàn', 'Đà Nẵng', 1, '<p class=\"MsoNormal\"><span style=\"font-size: 11.5pt; line-height: 107%; font-family: Arial, sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Đ&agrave; nẵng th&agrave;nh phố của những c&acirc;y cầu, đến đ&acirc;y du lịch bạn sẽ được chi&ecirc;m ngưỡng những c&acirc;y cầu lung linh v&agrave; huyền ảo về đ&ecirc;m. Một trong những c&acirc;y cầu nổi tiếng tại Đ&agrave; nẵng m&agrave; được những cặp đ&ocirc;i y&ecirc;u th&iacute;ch v&agrave; muốn c&ugrave;ng nhau trải nghiệm, hẹn ước đ&oacute; l&agrave; c&acirc;y cầu t&igrave;nh y&ecirc;u Đ&agrave; nẵng. H&ocirc;m nay, bạn v&agrave; dulichgiaretaihanoi sẽ c&ugrave;ng đi t&igrave;m hiểu chi tiết về c&acirc;y cầu n&agrave;y nh&eacute;!</span></p>', '<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-size: 11.5pt; line-height: 107%; font-family: Arial, sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Đ&agrave; nẵng th&agrave;nh phố của những c&acirc;y cầu, đến đ&acirc;y du lịch bạn sẽ được chi&ecirc;m ngưỡng những c&acirc;y cầu lung linh v&agrave; huyền ảo về đ&ecirc;m. Một trong những c&acirc;y cầu nổi tiếng tại Đ&agrave; nẵng m&agrave; được những cặp đ&ocirc;i y&ecirc;u th&iacute;ch v&agrave; muốn c&ugrave;ng nhau trải nghiệm, hẹn ước đ&oacute; l&agrave; c&acirc;y cầu t&igrave;nh y&ecirc;u Đ&agrave; nẵng. H&ocirc;m nay, bạn v&agrave; dulichgiaretaihanoi sẽ c&ugrave;ng đi t&igrave;m hiểu chi tiết về c&acirc;y cầu n&agrave;y nh&eacute;!</span><span style=\"font-size: 11.5pt; line-height: 107%; font-family: Arial, sans-serif;\"><br style=\"text-align: start;\" /><br style=\"text-align: start;\" /><!-- [if !supportLineBreakNewLine]--><br /><!--[endif]--></span></p>\r\n<h2 style=\"text-align: justify; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><strong><span style=\"font-family: Arial, sans-serif; font-weight: normal;\">Ngắm</span></strong><span style=\"font-family: Arial, sans-serif;\">&nbsp;<strong><span style=\"font-family: \'Arial\',\'sans-serif\'; font-weight: normal;\">cầu t&igrave;nh y&ecirc;u Đ&agrave; Nẵng&nbsp;l&atilde;ng mạn như trong phim H&agrave;n</span></strong></span></h2>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><!-- [if gte vml 1]><v:shapetype id=\"_x0000_t75\"\r\n coordsize=\"21600,21600\" o:spt=\"75\" o:preferrelative=\"t\" path=\"m@4@5l@4@11@9@11@9@5xe\"\r\n filled=\"f\" stroked=\"f\">\r\n <v:stroke joinstyle=\"miter\"/>\r\n <v:formulas>\r\n  <v:f eqn=\"if lineDrawn pixelLineWidth 0\"/>\r\n  <v:f eqn=\"sum @0 1 0\"/>\r\n  <v:f eqn=\"sum 0 0 @1\"/>\r\n  <v:f eqn=\"prod @2 1 2\"/>\r\n  <v:f eqn=\"prod @3 21600 pixelWidth\"/>\r\n  <v:f eqn=\"prod @3 21600 pixelHeight\"/>\r\n  <v:f eqn=\"sum @0 0 1\"/>\r\n  <v:f eqn=\"prod @6 1 2\"/>\r\n  <v:f eqn=\"prod @7 21600 pixelWidth\"/>\r\n  <v:f eqn=\"sum @8 21600 0\"/>\r\n  <v:f eqn=\"prod @7 21600 pixelHeight\"/>\r\n  <v:f eqn=\"sum @10 21600 0\"/>\r\n </v:formulas>\r\n <v:path o:extrusionok=\"f\" gradientshapeok=\"t\" o:connecttype=\"rect\"/>\r\n <o:lock v:ext=\"edit\" aspectratio=\"t\"/>\r\n</v:shapetype><v:shape id=\"Picture_x0020_7\" o:spid=\"_x0000_i1027\" type=\"#_x0000_t75\"\r\n alt=\"Cây cầu tình yêu Đà Nẵng lãng mạn bên bờ sông Hàn\" style=\'width:502.5pt;\r\n height:353.25pt;visibility:visible;mso-wrap-style:square\'>\r\n <v:imagedata src=\"file:///C:\\Users\\HP\\AppData\\Local\\Temp\\msohtmlclip1\\04\\clip_image001.jpg\"\r\n  o:title=\"Cây cầu tình yêu Đà Nẵng lãng mạn bên bờ sông Hàn\"/>\r\n</v:shape><![endif]--><!-- [if !vml]--><img src=\"file:///C:\\Users\\HP\\AppData\\Local\\Temp\\msohtmlclip1\\04\\clip_image002.jpg\" alt=\"C&acirc;y cầu t&igrave;nh y&ecirc;u Đ&agrave; Nẵng l&atilde;ng mạn b&ecirc;n bờ s&ocirc;ng H&agrave;n\" width=\"670\" height=\"471\" /><!--[endif]--><span style=\"font-size: 11.5pt; line-height: 107%; font-family: Arial, sans-serif;\"><br /><span style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"text-align: start;\">Cầu t&igrave;nh y&ecirc;u Đ&agrave; Nẵng nằm tr&ecirc;n s&ocirc;ng H&agrave;n với chiều d&agrave;i 68m. C&acirc;y cầu được thiết kệ dạng v&ograve;ng cung. Được kh&aacute;nh th&agrave;nh v&agrave;o giữa năm 2015, cầu t&igrave;nh y&ecirc;u &nbsp;ở Việt Nam đ&oacute;n một lượng lớn kh&aacute;ch thăm quan tới dự buổi lễ v&agrave; 200 chiếc kh&oacute;a t&igrave;nh y&ecirc;u được ph&aacute;t miễn ph&iacute; cũng nhanh ch&oacute;ng &ldquo;ch&aacute;y h&agrave;ng&rdquo; chỉ sau v&agrave;i giờ.</span></span><br style=\"text-align: start;\" /><span style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"text-align: start;\">Cầu kh&oacute;a t&igrave;nh y&ecirc;u ở Đ&agrave; Nẵng nằm giữa cầu Rồng v&agrave; cầu S&ocirc;ng H&agrave;n, hướng về ph&iacute;a giữa s&ocirc;ng. Từ điểm nh&igrave;n ấy, bạn c&oacute; thể ngắm nh&igrave;n quang cảnh xung quanh v&agrave; bờ b&ecirc;n kia của trung t&acirc;m th&agrave;nh phố.</span></span><br style=\"text-align: start;\" /><!-- [if !supportLineBreakNewLine]--><br /><!--[endif]--></span></p>\r\n<h3 style=\"text-align: justify; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><strong><span style=\"font-size: 12pt; font-family: Arial, sans-serif;\">Bạn đ&atilde; biết g&igrave; về cầu kh&oacute;a t&igrave;nh y&ecirc;u ở Đ&agrave; Nẵng?</span></strong></h3>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-size: 11.5pt; line-height: 107%; font-family: Arial, sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Cầu t&igrave;nh y&ecirc;u nằm ngay đầu cầu Rồng, đường Trần Hưng Đạo, quận Sơn Tr&agrave;, Đ&agrave; Nẵng. Đ&acirc;y ch&iacute;nh l&agrave; cụm c&ocirc;ng tr&igrave;nh điểm nhấn b&ecirc;n bờ s&ocirc;ng H&agrave;n của th&agrave;nh phố Đ&agrave; Nẵng &nbsp;mở cửa miễn ph&iacute; cho du kh&aacute;ch tham quan.</span><span style=\"font-size: 11.5pt; line-height: 107%; font-family: Arial, sans-serif;\"><br style=\"text-align: start;\" /><span style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"text-align: start;\">V&agrave;o buổi tối, h&agrave;ng trăm chiếc đ&egrave;n lồng đỏ h&igrave;nh tr&aacute;i tim được thắp s&aacute;ng v&agrave; trở n&ecirc;n lung linh, huyền ảo.</span></span><br style=\"text-align: start;\" /></span><!-- [if gte vml 1]><v:shape\r\n id=\"Picture_x0020_6\" o:spid=\"_x0000_i1026\" type=\"#_x0000_t75\" alt=\"Cây cầu tình yêu Đà Nẵng lãng mạn bên bờ sông Hàn\"\r\n style=\'width:485.25pt;height:353.25pt;visibility:visible;mso-wrap-style:square\'>\r\n <v:imagedata src=\"file:///C:\\Users\\HP\\AppData\\Local\\Temp\\msohtmlclip1\\04\\clip_image003.jpg\"\r\n  o:title=\"Cây cầu tình yêu Đà Nẵng lãng mạn bên bờ sông Hàn\"/>\r\n</v:shape><![endif]--><!-- [if !vml]--><img src=\"file:///C:\\Users\\HP\\AppData\\Local\\Temp\\msohtmlclip1\\04\\clip_image004.jpg\" alt=\"C&acirc;y cầu t&igrave;nh y&ecirc;u Đ&agrave; Nẵng l&atilde;ng mạn b&ecirc;n bờ s&ocirc;ng H&agrave;n\" width=\"647\" height=\"471\" /><!--[endif]--><span style=\"font-size: 11.5pt; line-height: 107%; font-family: Arial, sans-serif;\"><br /><span style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"text-align: start;\">Nếu ai đ&atilde; đến đ&acirc;y rồi th&igrave; chắc chắn sẽ th&iacute;ch khung cảnh của cầu T&igrave;nh y&ecirc;u ở Đ&agrave; Nẵng v&agrave;o buổi tối. Bởi v&igrave; khi ấy những chiếc đền lồng treo dọc theo th&acirc;n cầu bắt đầu s&aacute;ng đ&egrave;n, phản chiếu &aacute;nh s&aacute;ng đỏ long lanh xuống mặt s&ocirc;ng dập dềnh s&oacute;ng nước tạo n&ecirc;n cảnh vật v&ocirc; c&ugrave;ng l&atilde;ng mạn.</span></span><br style=\"text-align: start;\" /><!-- [if !supportLineBreakNewLine]--><br /><!--[endif]--></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\">&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-size: 11.5pt; line-height: 107%; font-family: \'Arial\',\'sans-serif\'; color: black; mso-themecolor: text1; background: white;\">Cầu t&igrave;nh y&ecirc;u lấy &yacute; tưởng từ những c&acirc;y cầu nổi tiếng tr&ecirc;n thế giới như: Pont des Arts (Ph&aacute;p), Hohenzollern (Đức), Tretriakovsky (Nga) hay Milvio (&Yacute;). Mặc d&ugrave; kh&aacute;c nhau về thiết kế nhưng giữa những c&acirc;y cầu n&agrave;y c&oacute; chung một &yacute; nghĩa, đ&oacute; l&agrave; những cặp đ&ocirc;i y&ecirc;u nhau sẽ t&igrave;m tới đ&acirc;y treo chiếc ổ kh&oacute;a c&oacute; khắc t&ecirc;n 2 người th&igrave; t&igrave;nh y&ecirc;u của họ sẽ m&atilde;i m&atilde;i bền chặt, thủy chung, kh&ocirc;ng g&igrave; c&oacute; thể khiến họ t&aacute;ch rời.</span><span style=\"font-size: 11.5pt; line-height: 107%; font-family: \'Arial\',\'sans-serif\'; color: black; mso-themecolor: text1;\"><br style=\"font-variant-ligatures: normal; font-variant-caps: normal; orphans: 2; text-align: start; widows: 2; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial; word-spacing: 0px;\" /></span><span style=\"color: black; mso-themecolor: text1; mso-no-proof: yes;\"><!-- [if gte vml 1]><v:shape\r\n id=\"Picture_x0020_5\" o:spid=\"_x0000_i1025\" type=\"#_x0000_t75\" alt=\"Cây cầu tình yêu Đà Nẵng lãng mạn bên bờ sông Hàn\"\r\n style=\'width:513pt;height:353.25pt;visibility:visible;mso-wrap-style:square\'>\r\n <v:imagedata src=\"file:///C:\\Users\\HP\\AppData\\Local\\Temp\\msohtmlclip1\\04\\clip_image005.jpg\"\r\n  o:title=\"Cây cầu tình yêu Đà Nẵng lãng mạn bên bờ sông Hàn\"/>\r\n</v:shape><![endif]--><!-- [if !vml]--><img src=\"file:///C:\\Users\\HP\\AppData\\Local\\Temp\\msohtmlclip1\\04\\clip_image006.jpg\" alt=\"C&acirc;y cầu t&igrave;nh y&ecirc;u Đ&agrave; Nẵng l&atilde;ng mạn b&ecirc;n bờ s&ocirc;ng H&agrave;n\" width=\"684\" height=\"471\" /><!--[endif]--></span><span style=\"font-size: 11.5pt; line-height: 107%; font-family: \'Arial\',\'sans-serif\'; color: black; mso-themecolor: text1;\"><br /><span style=\"background: white;\"><span style=\"font-variant-ligatures: normal; font-variant-caps: normal; orphans: 2; text-align: start; widows: 2; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial; float: none; word-spacing: 0px;\">Tới đ&acirc;y, bạn c&oacute; thể tự mang theo ổ kh&oacute;a của m&igrave;nh hoặc mua tại điểm b&aacute;n kh&oacute;a đặt ngay cạnh cầu. Dĩ nhi&ecirc;n l&agrave; những chiếc ổ kh&oacute;a ở đ&acirc;y cũng được đ&uacute;c th&agrave;nh h&igrave;nh tr&aacute;i tim &ndash; biểu tượng đặc trưng l&atilde;ng mạn nhất cho t&igrave;nh y&ecirc;u đ&ocirc;i lứa. Mỗi ổ kh&oacute;a sau khi được treo chắc chắn tr&ecirc;n th&agrave;nh cầu th&igrave; th&ocirc;ng thường người ta sẽ vứt hoặc giấu chiếc ch&igrave;a kh&oacute;a đ&oacute; đi để kh&ocirc;ng ai c&oacute; thể mở được.</span></span><br style=\"font-variant-ligatures: normal; font-variant-caps: normal; orphans: 2; text-align: start; widows: 2; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial; word-spacing: 0px;\" /><span style=\"background: white;\"><span style=\"font-variant-ligatures: normal; font-variant-caps: normal; orphans: 2; text-align: start; widows: 2; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial; float: none; word-spacing: 0px;\">Khi c&oacute; những sự kiện đặc biệt diễn ra như cuộc thi tr&igrave;nh diễn ph&aacute;o hoa quốc tế hoặc dịp lễ Tết, cầu t&igrave;nh y&ecirc;u Đ&agrave; Nẵng ch&iacute;nh l&agrave; địa điểm l&iacute; tưởng để chi&ecirc;m ngưỡng trọn vẹn những m&agrave;n ph&aacute;o hoa đặc sắc do c&oacute; view đẹp, rộng, tho&aacute;ng m&aacute;t v&agrave; quang đ&atilde;ng tuyệt đối.</span></span><br style=\"font-variant-ligatures: normal; font-variant-caps: normal; orphans: 2; text-align: start; widows: 2; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial; word-spacing: 0px;\" /><span style=\"background: white;\"><span style=\"font-variant-ligatures: normal; font-variant-caps: normal; orphans: 2; text-align: start; widows: 2; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial; float: none; word-spacing: 0px;\">Như vậy bạn cũng biết được qua c&acirc;y cầu t&igrave;nh y&ecirc;u qua giới thiệu của ch&uacute;ng t&ocirc;i trong b&agrave;i viết, nếu chưa được đặt ch&acirc;n đến th&agrave;nh phố n&agrave;y th&igrave; nhanh tay đặt cho m&igrave;nh tấm v&eacute; m&aacute;y bay đi Đ&agrave; nẵng tại c&ocirc;ng ty du lịch đại dương để trải nghiệm Đ&agrave; nẵng v&agrave; chi&ecirc;m ngưỡng c&acirc;y cầu t&igrave;nh y&ecirc;u c&ugrave;ng người m&igrave;nh y&ecirc;u nh&eacute;</span></span><br style=\"mso-special-character: line-break; font-variant-ligatures: normal; font-variant-caps: normal; orphans: 2; text-align: start; widows: 2; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial; word-spacing: 0px;\" /><!-- [if !supportLineBreakNewLine]--><br style=\"mso-special-character: line-break;\" /><!--[endif]--></span></p>', 'datingplaces-1544757857.jpg', '2018-12-13 20:24:17', 0),
(5, 'Đà Nẵng có ‘Khách sạn lãng mạn nhất thế giới’', 'Đà Nẵng', 1, '<p><span style=\"font-family: Arial, Helvetica, sans-serif; font-weight: bold; text-align: justify;\">Danh hiệu &ldquo;Kh&aacute;ch sạn l&atilde;ng mạn nhất thế giới&rdquo; nhận được sự ủng hộ của hơn 300.000 chuy&ecirc;n gia, du kh&aacute;ch, doanh nghiệp lữ h&agrave;nh tr&ecirc;n thế giới.</span></p>', '<p class=\"normalPara\" style=\"margin: 0px 0px 10px; padding: 0px; box-sizing: border-box; font-family: Arial, Helvetica, sans-serif; text-align: justify;\">Danh hiệu &ldquo;Kh&aacute;ch sạn l&atilde;ng mạn nhất thế giới&rdquo; nhận được sự ủng hộ của hơn 300.000 chuy&ecirc;n gia, du kh&aacute;ch, doanh nghiệp lữ h&agrave;nh tr&ecirc;n thế giới.</p>\r\n<table class=\"MASTERCMS_TPL_TABLE\" style=\"margin: 5px 0px 8px; padding: 5px; box-sizing: border-box; border-spacing: 0px; border-collapse: collapse; background-image: none; background-position: 0px 0px; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial; vertical-align: top; line-height: 14px; font-size: 12px; width: 510px; max-width: 100%; font-family: Arial, Helvetica, sans-serif; text-align: justify;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody style=\"margin: 0px; padding: 0px; box-sizing: border-box;\">\r\n<tr style=\"margin: 0px; padding: 0px; box-sizing: border-box;\">\r\n<td style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-align: center;\"><img style=\"margin: auto auto -3px; padding: 0px; box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; overflow: hidden; width: 510px; height: auto !important;\" title=\"Đ&agrave; Nẵng c&oacute; &lsquo;Kh&aacute;ch sạn l&atilde;ng mạn nhất thế giới&rsquo;\" src=\"http://thainguyentv.vn/stores/news_dataimages/camvan/112018/20/14/da-nang-co-khach-san-lang-man-nhat-the-gioi-04-.2825.jpg\" alt=\"da nang co khach san lang man nhat the gioi\" /></td>\r\n</tr>\r\n<tr style=\"margin: 0px; padding: 0px; box-sizing: border-box;\">\r\n<td style=\"margin: 0px; padding: 8px; box-sizing: border-box; background-color: #e5e5e5; font-style: italic; line-height: 1.6; text-align: center; font-family: Arial, Helvetica, sans-serif; font-size: 13px; color: #0c4da1 !important;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; text-align: justify;\">Vẻ đẹp của kh&aacute;ch sạn được c&ocirc;ng nhận l&agrave; &ldquo;Kh&aacute;ch sạn l&atilde;ng mạn nhất thế giới&rdquo; tại Đ&agrave; Nẵng</span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"normalPara\" style=\"margin: 0px 0px 10px; padding: 0px; box-sizing: border-box; font-family: Arial, Helvetica, sans-serif; text-align: justify;\">Vừa qua, tại đảo Bali (Indonesia), kh&aacute;ch sạn Mercure Danang French Village Bana Hills đ&atilde; đ&oacute;n nhận giải thưởng danh gi&aacute; &ldquo;Kh&aacute;ch sạn l&atilde;ng mạn nhất thế giới&rdquo; trong lĩnh vực dịch vụ kh&aacute;ch sạn (World luxury hotel awards).</p>\r\n<p class=\"normalPara\" style=\"margin: 0px 0px 10px; padding: 0px; box-sizing: border-box; font-family: Arial, Helvetica, sans-serif; text-align: justify;\">Sau 4 tuần tranh giải, để đi tới chiến thắng, Mercure Danang French Village Bana Hills đ&atilde; vượt qua rất nhiều ứng cử vi&ecirc;n s&aacute;ng gi&aacute; đến từ c&aacute;c ch&acirc;u lục để c&oacute; được sự ủng hộ của hơn 300.000 chuy&ecirc;n gia, du kh&aacute;ch, doanh nghiệp lữ h&agrave;nh tr&ecirc;n thế giới.</p>\r\n<p class=\"normalPara\" style=\"margin: 0px 0px 10px; padding: 0px; box-sizing: border-box; font-family: Arial, Helvetica, sans-serif; text-align: justify;\">Tọa lạc tr&ecirc;n đỉnh B&agrave; N&agrave; (Đ&agrave; Nẵng), Mercure Danang French Village Bana Hills l&agrave; c&ocirc;ng tr&igrave;nh kiến tr&uacute;c Gothic m&ocirc; phỏng c&aacute;c khu l&agrave;ng Ph&aacute;p cổ thế kỉ 19. C&aacute;c ph&ograve;ng nghỉ được ph&acirc;n bố tại 7 t&ograve;a nh&agrave;, mỗi t&ograve;a nh&agrave; như một cung điện nguy nga, lần lượt mang t&ecirc;n c&aacute;c th&agrave;nh phố nổi tiếng của nước Ph&aacute;p.</p>\r\n<p class=\"normalPara\" style=\"margin: 0px 0px 10px; padding: 0px; box-sizing: border-box; font-family: Arial, Helvetica, sans-serif; text-align: justify;\">Vinh danh những th&agrave;nh tựu đạt được trong ng&agrave;nh dịch vụ kh&aacute;ch sạn cao cấp, World luxury hotel awards l&agrave; hệ thống giải thưởng uy t&iacute;n bậc nhất thế giới, được đ&aacute;nh gi&aacute; l&agrave; đỉnh cao th&agrave;nh tựu m&agrave; tất c&aacute;c kh&aacute;ch sạn v&agrave; khu nghỉ dưỡng sang trọng to&agrave;n cầu vươn tới./.</p>', 'datingplaces-1544757970.jpg', '2018-12-13 20:26:10', 0),
(6, 'Lãng mạn cùng Mộc Miên Garden', 'Đà Nẵng', 1, '<p><span style=\"color: #222222; font-family: Arial; font-size: 15px;\">Đối với t&iacute;n đồ hay săn đ&oacute;n những qu&aacute;n c&agrave;-ph&ecirc; đẹp, mới xuất hiện tại Đ&agrave; Nẵng th&igrave; c&oacute; lẽ Mộc Mi&ecirc;n Garden Caf&eacute; nằm ở số 34 đường 2 th&aacute;ng 9, quận Hải Ch&acirc;u chẳng c&ograve;n lạ lẫm. Đ&uacute;ng như t&ecirc;n gọi của qu&aacute;n, Mộc Mi&ecirc;n Garden mang đến sự tho&aacute;ng đ&atilde;ng, lịch sự với những g&oacute;c ngồi dễ thương, dịu nhẹ.</span></p>', '<p style=\"padding: 0px 0px 16px; margin: 0px; list-style-type: none; border: 0px; font-size: 15px; color: #222222; line-height: 30px; font-family: Arial;\">Đối với t&iacute;n đồ hay săn đ&oacute;n những qu&aacute;n c&agrave;-ph&ecirc; đẹp, mới xuất hiện tại Đ&agrave; Nẵng th&igrave; c&oacute; lẽ Mộc Mi&ecirc;n Garden Caf&eacute; nằm ở số 34 đường 2 th&aacute;ng 9, quận Hải Ch&acirc;u chẳng c&ograve;n lạ lẫm. Đ&uacute;ng như t&ecirc;n gọi của qu&aacute;n, Mộc Mi&ecirc;n Garden mang đến sự tho&aacute;ng đ&atilde;ng, lịch sự với những g&oacute;c ngồi dễ thương, dịu nhẹ.</p>\r\n<table class=\"image center\" style=\"border-collapse: collapse; width: 622px; color: #135eb9; margin-bottom: 10px; font-family: Arial; font-size: 12px;\" width=\"620\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td style=\"text-align: center;\"><img style=\"border: 0px; max-width: 620px; height: 413px; width: 620px;\" src=\"https://www.baodanang.vn/dataimages/201601/original/images1220080_diachi.jpg\" alt=\"Kh&ocirc;ng gian dịu nhẹ tại Mộc Mi&ecirc;n Garden Caf&eacute;. Ảnh: H.L\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"image_desc\" style=\"padding: 5px;\">Kh&ocirc;ng gian dịu nhẹ tại Mộc Mi&ecirc;n Garden Caf&eacute;. Ảnh: H.L</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"padding: 0px 0px 16px; margin: 0px; list-style-type: none; border: 0px; font-size: 15px; color: #222222; line-height: 30px; font-family: Arial;\">Nếu nh&igrave;n từ hướng đường 2 th&aacute;ng 9, sẽ kh&oacute; nhận ra c&oacute; một Mộc Mi&ecirc;n Garden Caf&eacute; bề thế v&agrave; nổi bật với diện t&iacute;ch hơn 1.000m<sup>2</sup>. Đ&acirc;y c&oacute; lẽ l&agrave; qu&aacute;n c&agrave;-ph&ecirc; s&acirc;n vườn c&oacute; nhiều điểm cộng nằm tr&ecirc;n một con đường sầm uất của th&agrave;nh phố Đ&agrave; Nẵng.</p>\r\n<p style=\"padding: 0px 0px 16px; margin: 0px; list-style-type: none; border: 0px; font-size: 15px; color: #222222; line-height: 30px; font-family: Arial;\">Vị tr&iacute; đẹp, b&agrave;n ghế được bố tr&iacute; hợp l&yacute; cho từng kh&ocirc;ng gian trong v&agrave; ngo&agrave;i s&acirc;n vườn. Cụ thể, nằm giữa những d&atilde;y b&agrave;n ghế l&agrave; hồ c&aacute; rộng r&atilde;i với h&agrave;ng trăm ch&uacute; c&aacute; bơi lượn. Những vi&ecirc;n đ&aacute; cụi trắng tr&ograve;n được viền quanh m&eacute;p hồ, gi&agrave;n hoa giấy đung đưa theo gi&oacute; cho những b&uacute;p nụ trắng hồng, điểm xuyết xung quanh l&agrave; những gi&ograve; lan, gi&ograve; c&acirc;y cảnh xanh mượt, mang lại cho kh&aacute;ch cảm gi&aacute;c thư gi&atilde;n tuyệt đối. Dường như, khi ngồi ở địa chỉ n&agrave;y, kh&aacute;ch h&agrave;ng kh&ocirc;ng c&ograve;n cảm gi&aacute;c ngột ngạt bởi những bức tường b&ecirc;-t&ocirc;ng do kh&ocirc;ng gian được thiết kế rất gợi mở v&agrave; thanh tho&aacute;t.</p>\r\n<p style=\"padding: 0px 0px 16px; margin: 0px; list-style-type: none; border: 0px; font-size: 15px; color: #222222; line-height: 30px; font-family: Arial;\">Mộc Mi&ecirc;n Garden Caf&eacute; được chia l&agrave;m nhiều khu vực, một phần cho khung cảnh c&agrave;-ph&ecirc; s&acirc;n vườn, một phần kh&ocirc;ng gian c&ograve;n lại l&agrave; khu vực m&aacute;y lạnh hiện đại cho kh&aacute;ch ngồi l&agrave;m việc, thảo luận nh&oacute;m. T&ugrave;y từng mục đ&iacute;ch, số lượng m&agrave; bạn c&oacute; thể lựa chọn chỗ ngồi ph&ugrave; hợp cho m&igrave;nh v&agrave; người th&acirc;n, bạn b&egrave;. Kh&ocirc;ng chỉ ăn điểm bởi kh&ocirc;ng gian l&atilde;ng mạn, địa chỉ n&agrave;y c&ograve;n ch&uacute; &yacute; đến từng chi tiết nhỏ, từ m&agrave;u sắc của những chiếc ghế đệm, đến m&agrave;u trắng thanh tho&aacute;t hợp với khung cảnh s&acirc;n vườn.</p>\r\n<p style=\"padding: 0px 0px 16px; margin: 0px; list-style-type: none; border: 0px; font-size: 15px; color: #222222; line-height: 30px; font-family: Arial;\">C&oacute; lẽ, những người kh&ocirc;ng th&iacute;ch sự x&ocirc; bồ của qu&aacute;n x&aacute;, th&igrave; Mộc Mi&ecirc;n Garden Caf&eacute; l&agrave; một địa chỉ th&uacute; vị để gh&eacute; đến, để h&ograve;a m&igrave;nh giữa sự dịu nhẹ của l&agrave;n nước trong v&agrave; c&acirc;y cỏ xung quanh.</p>\r\n<p style=\"padding: 0px 0px 16px; margin: 0px; list-style-type: none; border: 0px; font-size: 15px; color: #222222; line-height: 30px; font-family: Arial;\">Với những điểm cộng kể tr&ecirc;n, Mộc Mi&ecirc;n Garden Caf&eacute; hiện l&agrave; qu&aacute;n c&agrave;-ph&ecirc; được nhiều người y&ecirc;u th&iacute;ch tại Đ&agrave; Nẵng. Đ&acirc;y cũng l&agrave; địa điểm th&iacute;ch hợp để tụ tập bạn b&egrave;, tr&ograve; chuyện, ngắm đ&agrave;n c&aacute; hay t&igrave;m kh&ocirc;ng gian thư gi&atilde;n trong những ng&agrave;y cuối tuần.</p>', 'datingplaces-1544758082.jpg', '2018-12-13 20:28:02', 0),
(7, 'GHÉ THĂM “KHÁCH SẠN LÃNG MẠN NHẤT THẾ GIỚI” Ở ĐÀ NẴNG', 'Đà Nẵng', 2, '<p><span style=\"color: #2b2b2b; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\', \'Noto Color Emoji\'; font-size: 15.2px; font-weight: 600;\">Kh&aacute;ch sạn Mercure Danang French Village Bana Hills từng nhận giải thưởng danh gi&aacute; &ldquo;Kh&aacute;ch sạn l&atilde;ng mạn nhất thế giới&rdquo;.</span></p>', '<div class=\"block-description\" style=\"box-sizing: border-box; font-weight: 600; margin-bottom: 1rem; font-size: 0.95rem; color: #2b2b2b; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\', \'Noto Color Emoji\';\">Kh&aacute;ch sạn Mercure Danang French Village Bana Hills từng nhận giải thưởng danh gi&aacute; &ldquo;Kh&aacute;ch sạn l&atilde;ng mạn nhất thế giới&rdquo;.</div>\r\n<div class=\"block-timeline\" style=\"box-sizing: border-box; color: #2b2b2b; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\', \'Noto Color Emoji\'; font-size: 17.6px;\">&nbsp;</div>\r\n<div class=\"block-thumb mb-3\" style=\"box-sizing: border-box; color: #2b2b2b; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\', \'Noto Color Emoji\'; font-size: 17.6px; margin-bottom: 1rem !important;\"><img style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; cursor: crosshair;\" src=\"http://truyenhinhdulich.vn/upload/news/577_ghe_tham_khach_san_lang_man_nhat_the_gioi.jpg\" alt=\"Gh&eacute; thăm &ldquo;kh&aacute;ch sạn l&atilde;ng mạn nhất thế giới&rdquo; ở Đ&agrave; Nẵng\" /></div>\r\n<div class=\"block-content\" style=\"box-sizing: border-box; color: #2b2b2b; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\', \'Noto Color Emoji\'; font-size: 17.6px;\">\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; text-align: justify;\"><span style=\"font-size: 12pt;\">L&agrave; kh&aacute;ch sạn duy nhất tọa lạc tr&ecirc;n đỉnh B&agrave; N&agrave;,&nbsp;<a style=\"box-sizing: border-box; color: #ff3a56; text-decoration-line: none; background-color: transparent; transition: all 0.5s ease 0s;\" href=\"http://truyenhinhdulich.net/cam-nang-du-lich/nhung-dia-diem-khong-the-bo-qua-khi-den-da-nang-430.html\" target=\"_blank\" rel=\"noopener noreferrer\">Đ&agrave; Nẵng</a>, thuộc tổ hợp khu du lịch sinh th&aacute;i &ndash; nghỉ dưỡng B&agrave; N&agrave; Hills, Mercure Danang French Village Bana Hills l&agrave; c&ocirc;ng tr&igrave;nh kiến tr&uacute;c Gothic m&ocirc; phỏng c&aacute;c khu l&agrave;ng Ph&aacute;p cổ thế kỉ 19.&nbsp;</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; text-align: center;\"><img class=\"fr-fic fr-dib\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; cursor: crosshair;\" src=\"http://truyenhinhdulich.net/upload/news/11_2018/2055333678b3025a9f62629d4b0ac9d5.jpg\" data-errcode=\"1\" data-errmsg=\"Tải ảnh th&agrave;nh c&ocirc;ng !\" /><em style=\"box-sizing: border-box; padding-top: 5px; padding-bottom: 5px; display: block;\">Kh&aacute;ch sạn mang đậm dấu ấn kiến tr&uacute;c Ph&aacute;p.</em><em style=\"box-sizing: border-box; padding-top: 5px; padding-bottom: 5px; display: block;\"><img class=\"fr-fic fr-dib\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; margin-bottom: 5px; cursor: crosshair; width: 730px;\" src=\"http://truyenhinhdulich.net/upload/news/11_2018/8a8bd05f03a866837064a4269b51e6cd.jpg\" data-errcode=\"1\" data-errmsg=\"Tải ảnh th&agrave;nh c&ocirc;ng !\" /></em><em style=\"box-sizing: border-box; padding-top: 5px; padding-bottom: 5px; display: block;\">C&ocirc;ng tr&igrave;nh m&ocirc; phỏng nh&agrave; thờ ở Ph&aacute;p.</em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; text-align: justify;\"><span style=\"font-size: 12pt;\">Gần 500 ph&ograve;ng nghỉ được ph&acirc;n bố tại 7 t&ograve;a nh&agrave;, mỗi t&ograve;a nh&agrave; lại mang t&ecirc;n một th&agrave;nh phố nổi tiếng của nước Ph&aacute;p như Hotel de Paris, Hotel de Lyon hay Hotel de Bordeaux&hellip; Mỗi ph&ograve;ng tại đ&acirc;y đều c&oacute; cửa sổ nh&igrave;n ra n&uacute;i hay khu l&agrave;ng Ph&aacute;p cổ gần đ&oacute;. Nghỉ dưỡng ở đ&acirc;y, du kh&aacute;ch sẽ cảm nhận được vẻ đẹp của thi&ecirc;n nhi&ecirc;n, kh&ocirc;ng những vậy c&ograve;n thấy t&acirc;m hồn m&igrave;nh thư th&aacute;i hơn với trải nghiệm hồ bơi bốn m&ugrave;a độc đ&aacute;o.&nbsp;</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; text-align: center;\"><img class=\"fr-fic fr-dib\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; cursor: crosshair;\" src=\"http://truyenhinhdulich.net/upload/news/11_2018/5df609c55a698ab213d0be301569775e.jpg\" data-errcode=\"1\" data-errmsg=\"Tải ảnh th&agrave;nh c&ocirc;ng !\" /><em style=\"box-sizing: border-box; padding-top: 5px; padding-bottom: 5px; display: block;\">Ph&ograve;ng hạng Super Deluxe.</em><em style=\"box-sizing: border-box; padding-top: 5px; padding-bottom: 5px; display: block;\"><img class=\"fr-fic fr-dib\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; margin-bottom: 5px; cursor: crosshair;\" src=\"http://truyenhinhdulich.net/upload/news/11_2018/74687ea1b98d6370508fb61f2a68e30a.jpg\" data-errcode=\"1\" data-errmsg=\"Tải ảnh th&agrave;nh c&ocirc;ng !\" /></em><em style=\"box-sizing: border-box; padding-top: 5px; padding-bottom: 5px; display: block;\">Ph&ograve;ng hạng Deluxe.</em><em style=\"box-sizing: border-box; padding-top: 5px; padding-bottom: 5px; display: block;\"><img class=\"fr-fic fr-dib\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; margin-bottom: 5px; cursor: crosshair;\" src=\"http://truyenhinhdulich.net/upload/news/11_2018/83524350448b9fe9ac2177c6662f45b1.jpg\" data-errcode=\"1\" data-errmsg=\"Tải ảnh th&agrave;nh c&ocirc;ng !\" /></em><em style=\"box-sizing: border-box; padding-top: 5px; padding-bottom: 5px; display: block;\">Một ph&ograve;ng nghỉ c&oacute; cửa sổ hướng ra khu l&agrave;ng Ph&aacute;p.</em><em style=\"box-sizing: border-box; padding-top: 5px; padding-bottom: 5px; display: block;\"><img class=\"fr-fic fr-dib\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; margin-bottom: 5px; cursor: crosshair;\" src=\"http://truyenhinhdulich.net/upload/news/11_2018/46d336f9e1a7699ab969e0df243317ae.jpg\" data-errcode=\"1\" data-errmsg=\"Tải ảnh th&agrave;nh c&ocirc;ng !\" /></em><em style=\"box-sizing: border-box; padding-top: 5px; padding-bottom: 5px; display: block;\">Hồ bơi bốn m&ugrave;a.</em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; text-align: justify;\"><span style=\"font-size: 12pt;\">Ngắm ho&agrave;ng h&ocirc;n hay b&igrave;nh minh trong khung cảnh thi&ecirc;n nhi&ecirc;n c&oacute; một kh&ocirc;ng hai, thưởng thức ẩm thực khắp thế giới, tham gia c&aacute;c lễ hội, tận hưởng tiện &iacute;ch 4 sao theo ti&ecirc;u chuẩn quốc tế chắc chắn sẽ l&agrave; một kỳ nghỉ ho&agrave;n hảo với bất kỳ một du kh&aacute;ch n&agrave;o.</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; text-align: center;\"><img class=\"fr-fic fr-dib\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; cursor: crosshair; width: 730px;\" src=\"http://truyenhinhdulich.net/upload/news/11_2018/e7c42589103c0ebff46c1c81e86bb3b0.jpg\" data-errcode=\"1\" data-errmsg=\"Tải ảnh th&agrave;nh c&ocirc;ng !\" /><em style=\"box-sizing: border-box; padding-top: 5px; padding-bottom: 5px; display: block;\">Một hoạt động trong lễ hội bia.</em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; text-align: center;\"><em style=\"box-sizing: border-box;\"><img class=\"fr-fic fr-dib\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; margin-bottom: 5px; cursor: crosshair; width: 730px;\" src=\"http://truyenhinhdulich.net/upload/news/11_2018/564b480313ed7d5dad573358bea67cce.jpg\" data-errcode=\"1\" data-errmsg=\"Tải ảnh th&agrave;nh c&ocirc;ng !\" /></em><em style=\"box-sizing: border-box;\">Kh&ocirc;ng kh&iacute; lễ hội lu&ocirc;n ngập tr&agrave;n.</em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; text-align: center;\"><em style=\"box-sizing: border-box;\"><img class=\"fr-fic fr-dib\" style=\"box-sizing: border-box; vertical-align: middle; border-style: none; max-width: 100%; margin-bottom: 5px; cursor: crosshair;\" src=\"http://truyenhinhdulich.net/upload/news/11_2018/20d245763cefc1855bed80f25bcec4d5.jpg\" data-errcode=\"1\" data-errmsg=\"Tải ảnh th&agrave;nh c&ocirc;ng !\" /></em><em style=\"box-sizing: border-box;\">Thưởng thức ẩm thực của nhiều quốc gia.</em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; text-align: justify;\"><span style=\"font-size: 12pt;\">Mang đậm phong c&aacute;ch Ph&aacute;p, Mercure Danang French Village Bana Hills l&agrave; sự lựa chọn tuyệt vời cho c&aacute;c cặp đ&ocirc;i muốn c&oacute; một kỳ nghỉ ri&ecirc;ng tư, v&agrave; xứng đ&aacute;ng với danh hiệu &ldquo;Kh&aacute;ch sạn l&atilde;ng mạn nhất thế giới&rdquo;.</span></p>\r\n</div>', 'datingplaces-1544758283.jpg', '2018-12-13 20:31:23', 0),
(8, 'Ghi dấu tình yêu với cây đa ngàn năm tuổi', 'Đà Nẵng', 4, '<p><span style=\"color: #404040; font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 13px; text-align: justify;\">C&acirc;y đa ng&agrave;n năm tuổi nằm cạnh con đường chạy dọc triền n&uacute;i ph&iacute;a Đ&ocirc;ng b&aacute;n đảo Sơn Tr&agrave;, thuộc khu bảo tồn thi&ecirc;n nhi&ecirc;n Sơn Tr&agrave;</span></p>', '<ol style=\"border: 0px; font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 13px; margin: 0px 0px 20px 3em; outline: 0px; padding: 0px; vertical-align: baseline; list-style-position: initial; list-style-image: initial; color: #404040; text-align: justify;\" start=\"2\">\r\n<li style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline; text-align: left;\"><span style=\"font-weight: bold;\">Ghi dấu t&igrave;nh y&ecirc;u với c&acirc;y đa ng&agrave;n năm tuổi</span><br /><a style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline; color: #117bb8;\" href=\"https://dulichqag.files.wordpress.com/2015/02/anh-cuoi-ban-dao-son-tra-2.jpg\"><img class=\"aligncenter wp-image-56 size-full\" style=\"height: auto; max-width: 100%; border: 0px; clear: both; display: block; margin: 20px auto;\" title=\"Du lịch Đ&agrave; Nẵng &ndash; Hội An với những địa điểm chụp ảnh cưới l&atilde;ng mạn\" src=\"https://dulichqag.files.wordpress.com/2015/02/anh-cuoi-ban-dao-son-tra-2.jpg?w=700\" sizes=\"(max-width: 600px) 100vw, 600px\" srcset=\"https://dulichqag.files.wordpress.com/2015/02/anh-cuoi-ban-dao-son-tra-2.jpg 600w, https://dulichqag.files.wordpress.com/2015/02/anh-cuoi-ban-dao-son-tra-2.jpg?w=150 150w, https://dulichqag.files.wordpress.com/2015/02/anh-cuoi-ban-dao-son-tra-2.jpg?w=300 300w\" alt=\"Du lịch Đ&agrave; Nẵng &ndash; Hội An với những địa điểm chụp ảnh cưới l&atilde;ng mạn\" data-attachment-id=\"56\" data-permalink=\"https://dulichqag.wordpress.com/2015/02/26/du-lich-da-nang-hoi-an-voi-nhung-dia-diem-chup-anh-cuoi-lang-man/anh-cuoi-ban-dao-son-tra-2/\" data-orig-file=\"https://dulichqag.files.wordpress.com/2015/02/anh-cuoi-ban-dao-son-tra-2.jpg?w=700\" data-orig-size=\"600,400\" data-comments-opened=\"1\" data-image-meta=\"{&quot;aperture&quot;:&quot;0&quot;,&quot;credit&quot;:&quot;&quot;,&quot;camera&quot;:&quot;&quot;,&quot;caption&quot;:&quot;&quot;,&quot;created_timestamp&quot;:&quot;0&quot;,&quot;copyright&quot;:&quot;&quot;,&quot;focal_length&quot;:&quot;0&quot;,&quot;iso&quot;:&quot;0&quot;,&quot;shutter_speed&quot;:&quot;0&quot;,&quot;title&quot;:&quot;&quot;,&quot;orientation&quot;:&quot;1&quot;}\" data-image-title=\"anh-cuoi-ban-dao-son-tra-2\" data-image-description=\"\" data-medium-file=\"https://dulichqag.files.wordpress.com/2015/02/anh-cuoi-ban-dao-son-tra-2.jpg?w=700?w=300\" data-large-file=\"https://dulichqag.files.wordpress.com/2015/02/anh-cuoi-ban-dao-son-tra-2.jpg?w=700?w=600\" /></a><span style=\"font-weight: bold;\"><br /></span></li>\r\n</ol>\r\n<p style=\"border: 0px; font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 13px; margin: 0px 0px 1.5em; outline: 0px; padding: 0px; vertical-align: baseline; color: #404040; text-align: justify;\">C&acirc;y đa ng&agrave;n năm tuổi nằm cạnh con đường chạy dọc triền n&uacute;i ph&iacute;a Đ&ocirc;ng b&aacute;n đảo Sơn Tr&agrave;, thuộc khu bảo tồn thi&ecirc;n nhi&ecirc;n Sơn Tr&agrave;. Đ&acirc;y kh&ocirc;ng chỉ l&agrave; địa điểm tham quan hấp dẫn m&agrave; c&ograve;n l&agrave; nơi ghi dấu t&igrave;nh y&ecirc;u của c&aacute;c cặp đ&ocirc;i bằng những shoot h&igrave;nh cưới. Khi tia nắng long lanh xuy&ecirc;n qua t&aacute;n l&aacute; c&acirc;y hay khi m&agrave;n sương mờ bao phủ đều mang lại cho kh&ocirc;ng gian một vẻ đẹp huyền ảo. Sự trường tồn của c&acirc;y đa cũng khiến ta li&ecirc;n tưởng đến t&igrave;nh y&ecirc;u vững bền của đ&ocirc;i lứa.</p>', 'datingplaces-1544758644.jpg', '2018-12-13 20:37:24', 0);
